import React, { useEffect, useState } from "react";
import { QRCodeCanvas } from "qrcode.react"; // ✅ QR code library

// JSONPlaceholder API (gives free dummy data)
const BASE_URL = "https://jsonplaceholder.typicode.com";

export default function CardList() {
  const [posts, setPosts] = useState([]); // Store all posts
  const [isLoading, setIsLoading] = useState(true); // Loading state
  const [error, setError] = useState(null); // Error state

  // Fetch data when component loads
  useEffect(() => {
    const fetchPosts = async () => {
      try {
        const response = await fetch(`${BASE_URL}/posts`); // API call
        const data = await response.json(); // Convert response to JSON
        setPosts(data.slice(0, 10)); // Only take 10 posts (to keep UI clean)
      } catch (err) {
        setError("Failed to fetch data.");
      } finally {
        setIsLoading(false);
      }
    };

    fetchPosts();
  }, []);

  if (isLoading) return <div>Loading...</div>; // Show loading text
  if (error) return <div>{error}</div>; // Show error message

  return (
    <div
      style={{
        display: "grid",
        gridTemplateColumns: "repeat(auto-fill, minmax(250px, 1fr))",
        gap: "20px",
        padding: "20px",
      }}
    >
      {posts.map((post, index) => (
        <div
          key={post.id}
          style={{
            border: "1px solid #ccc",
            borderRadius: "10px",
            padding: "15px",
            textAlign: "center",
            boxShadow: "0px 4px 6px rgba(0,0,0,0.1)",
          }}
        >
          {/* Dummy image */}
          <img
            src={`https://picsum.photos/200/150?random=${post.id}`}
            alt="Random"
            style={{
              borderRadius: "8px",
              width: "100%",
              height: "150px",
              objectFit: "cover",
            }}
          />

          {/* Index number */}
          <h3 style={{ margin: "10px 0", color: "gray" }}>#{index + 1}</h3>

          {/* Title */}
          <h2 style={{ fontSize: "18px", margin: "10px 0" }}>{post.title}</h2>

          {/* Description */}
          <p style={{ fontSize: "14px", color: "#555" }}>{post.body}</p>

          {/* QR Code */}
          <div style={{ marginTop: "10px" }}>
            <QRCodeCanvas value={`Post ID: ${post.id}`} size={80} />
          </div>
        </div>
      ))}
    </div>
  );
}
