import React, { useState, useEffect, useRef } from "react";
import { QRCodeCanvas } from "qrcode.react"; // ✅ fixed import

const BASE_URL = "https://jsonplaceholder.typicode.com";

export default function Demo() {
  // State variables
  const [posts, setPosts] = useState([]); // Stores data from API
  const [isLoading, setIsLoading] = useState(false); // Tracks loading state
  const [error, setError] = useState(null); // Stores error if fetch fails
  const [page, setPage] = useState(0); // Pagination (current page)

  // Ref to store AbortController to cancel previous requests
  const abortControllerRef = useRef(null);

  // useEffect runs when component loads or page changes
  useEffect(() => {
    const fetchPosts = async () => {
      // Cancel previous request if still running
      abortControllerRef.current?.abort();
      abortControllerRef.current = new AbortController();

      setIsLoading(true); // Show loading indicator

      try {
        // Fetch data from API (6 posts per page)
        const response = await fetch(
          `${BASE_URL}/posts?_limit=6&_page=${page + 1}`,
          { signal: abortControllerRef.current.signal }
        );

        const data = await response.json(); // Convert response to JSON
        setPosts(data); // Save data to state
      } catch (e) {
        if (e.name === "AbortError") {
          console.log("Request cancelled");
          return; // Ignore aborted requests
        }
        setError(e); // Save other errors
      } finally {
        setIsLoading(false); // Stop loading indicator
      }
    };

    fetchPosts();
  }, [page]); // Runs whenever 'page' changes

  // Show error message if fetch failed
  if (error) {
    return <div>Something went wrong! Please try again.</div>;
  }

  return (
    <div className="p-6">
      {/* Heading */}
      <h1 className="mb-4 text-2xl font-bold">Posts as Cards</h1>

      {/* Pagination Button */}
      <button
        className="mb-4 px-4 py-2 bg-blue-500 text-white rounded hover:bg-blue-600"
        onClick={() => setPage(page + 1)}
      >
        Next Page ({page + 1})
      </button>

      {/* Loading indicator */}
      {isLoading && <div>Loading...</div>}

      {/* Cards Grid */}
      <div className="grid grid-cols-1 sm:grid-cols-2 md:grid-cols-3 gap-6">
        {posts.map((post, index) => (
          <div
            key={post.id}
            className="rounded-2xl shadow-lg overflow-hidden bg-white hover:shadow-xl transition-shadow p-4"
          >
            {/* Random Image */}
            <img
              className="w-full h-48 object-cover rounded-md mb-3"
              src={`https://picsum.photos/400/200?random=${post.id}`}
              alt={post.title}
            />

            {/* Title & Description */}
            <h2 className="text-xl font-semibold mb-2">{post.title}</h2>
            <p className="text-gray-600 text-sm mb-3">{post.body}</p>

            {/* Index */}
            <div className="mb-3 text-gray-800 font-medium">
              Index: {index + 1}
            </div>

            {/* QR Code */}
            <div className="flex justify-center">
              <QRCodeCanvas value={`Post ID: ${post.id}`} size={100} />
            </div>
          </div>
        ))}
      </div>
    </div>
  );
}
